mapper.select:

select

  distinct


from advertisement_channel




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

select
distinct
,ifnull(channel_sum.regist_count,0) regist_count, ifnull(channel_sum.first_count,0) first_count, ifnull(channel_sum.first_amount,0) first_amount, ifnull(channel_sum.repeat_count,0) repeat_count, ifnull(channel_sum.repeat_amount,0) repeat_amount from advertisement_channel left join ( select user.channel_id, count(user.channel_id) regist_count, count(first_investment.user_id) first_count, ifnull(sum(first_investment.amount),0) first_amount, ifnull(sum(investment_sum.count>1),0) repeat_count, ifnull(sum(investment_sum.amount),0) repeat_amount from user left join( select user_id, sum(amount) amount, count(investment.id) count from investment where pay_status=2 group by user_id )investment_sum on investment_sum.user_id=user.id left join( select investment.* from investment inner join ( select min(id) id from investment where pay_status=2 group by user_id )first_investment on investment.id=first_investment.id )first_investment on first_investment.user_id=user.id where user.channel_id is not null
and user.create_date>=#{params.userCreateDateStart,jdbcType=TIMESTAMP}
and user.create_date < #{params.userCreateDateEnd,jdbcType=TIMESTAMP}
and user.create_date is not null group by user.channel_id )channel_sum on advertisement_channel.id=channel_sum.channel_id
order by ${example.orderByClause}
limit ${example.limitStart} , ${example.limitEnd}
