mapper.select:

select

  distinct


from app_notice




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

SELECT count(an.id)
      from (
        SELECT
            a.*
        FROM
            app_notice a,
            user u
        WHERE
            DATE_FORMAT(a.create_date, '%Y-%m-%d')   >=   DATE_FORMAT(u.create_date, '%Y-%m-%d')
        AND u.id = #{userId}
    ) an left JOIN app_notice_reader anr on an.id = anr.notice_id and
        anr.user_id=#{userId}
      WHERE
an.status = 1;

select *
from
app_notice a 
where 1 = 1

    and a.status=#{status}


    and a.is_show=#{isShow}

order by a.send_date desc

    limit #{start},#{limit}
