mapper.select:

select

  distinct


from t_goods_order_detail




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

select god.id, god.order_id, god.create_date, go.order_no, go.user_id, u.true_name, gc.category_name, g.goods_name, god.count, god.sale_price, god.balance_pay_money, god.cash_pay_money, god.credit_pay_money, god.hongbao_money, r2.create_date as pay_date, r2.status as pay_status, r2.remark, go.state as order_status, go.pay_time, go.refund_finish_time from t_goods_order_detail god inner join goods_order_view go on go.id = god.order_id inner join user u on u.id = go.user_id
inner join admin_responsible on admin_responsible.admin_id=${adminId} and admin_responsible.department_id=u.department_id
inner join department_relation on department_relation.department_id=${departmentId} and department_relation.sub_department_id=u.department_id
inner join t_goods g on g.id = god.goods_id inner join t_goods_category gc on gc.id = g.category_id left join (select other_id,max(id) as max_id from recharge where order_type = 'goods' group by other_id) r1 on r1.other_id = go.id left join recharge r2 on r2.id = r1.max_id where 1 = 1
and u.true_name like '%${trueName}%'
and go.order_no like '%${keyword}%'
and go.state = #{orderStatus}
and r2.status = #{payStatus}
and gc.category_name = #{goodsCategory}
and god.create_date >= #{startTime}
and god.create_date < #{endTime}
order by god.create_date desc
limit #{limitStart},#{limitEnd}

  		select count(*)
		from t_goods_order_detail god
		inner join goods_order_view go on go.id = god.order_id
		inner join user u on u.id = go.user_id

			inner join admin_responsible 
				on admin_responsible.admin_id=${adminId} 
				and admin_responsible.department_id=u.department_id


	    	inner join department_relation
				on department_relation.department_id=${departmentId} 
				and department_relation.sub_department_id=u.department_id

		inner join t_goods g on g.id = god.goods_id
		inner join t_goods_category gc on gc.id = g.category_id	
		left join (select other_id,max(id) as max_id from recharge where order_type = 'goods' group by other_id) r1 on r1.other_id = go.id
		left join recharge r2 on r2.id = r1.max_id
		where 1 = 1

  		and u.true_name like '%${trueName}%'


  		and go.order_no like '%${keyword}%'


  		and go.state = #{orderStatus}


  		and r2.status = #{payStatus}


  		and gc.category_name = #{goodsCategory}


	  	and god.create_date >= #{startTime}


  		and god.create_date < #{endTime}
