select * from ( SELECT CONCAT(SUBSTRING(u.phone, 1, 3), '**', SUBSTRING(u.phone,CHAR_LENGTH(u.phone)-3, 4)) phone, iua.user_id,iua.award_type awardType,iua.amount,iua.obtain_conditions obtainConditions,iua.create_time createTime, CONCAT(SUBSTRING(u.true_name, 1, 1), '**', SUBSTRING(u.true_name,CHAR_LENGTH(u.true_name), 2)) trueName, CONCAT(SUBSTRING(u2.true_name, 1, 1), '**', SUBSTRING(u2.true_name,CHAR_LENGTH(u2.true_name), 2)) investmentTrueName, i.type FROM invite_user_acitvity_award_log iua LEFT JOIN USER u ON iua.user_id=u.id LEFT JOIN investment i ON iua.investment_id=i.id LEFT JOIN USER u2 ON i.user_id =u2.id having i.type = 0
and iua.user_id = #{userId}
and iua.award_type != 'first_investee' union all SELECT CONCAT(SUBSTRING(u.phone, 1, 3), '**', SUBSTRING(u.phone,CHAR_LENGTH(u.phone)-3, 4)) phone, iua.user_id,iua.award_type awardType,iua.amount,iua.obtain_conditions obtainConditions,iua.create_time createTime, CONCAT(SUBSTRING(u.true_name, 1, 1), '**', SUBSTRING(u.true_name,CHAR_LENGTH(u.true_name), 2)) trueName, CONCAT(SUBSTRING(u2.true_name, 1, 1), '**', SUBSTRING(u2.true_name,CHAR_LENGTH(u2.true_name), 2)) investmentTrueName, 8 FROM invite_user_acitvity_award_log iua LEFT JOIN USER u ON iua.user_id=u.id inner join monthly_gain_interest mgi on iua.investment_id = mgi.id LEFT JOIN USER u2 ON mgi.user_id =u2.id having 1 = 1
and iua.user_id = #{userId}
and iua.award_type != 'first_investee') t order by t.createTime desc
limit #{limit}
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

SELECT u.true_name as trueName, u.phone, p.title, il.amount as rewardAmount, i.amount as inviteAmount, il.award_type as awardType, il.create_time FROM invite_user_acitvity_award_log il LEFT JOIN investment i ON il.investment_id = i.id LEFT JOIN project p on i.project_id = p.id LEFT JOIN user u on i.user_id = u.id where 1=1 and il.award_type != 'first_investee' and i.`type` not in (8,9) and il.user_id = #{userId} union SELECT u.true_name as trueName, u.phone, mgpp.title, il.amount as rewardAmount, i.amount as inviteAmount, il.award_type as awardType, il.create_time FROM invite_user_acitvity_award_log il LEFT JOIN investment i ON il.investment_id = i.id LEFT JOIN monthly_gain_project_package mgpp on i.package_id = mgpp.id LEFT JOIN user u on i.user_id = u.id where 1=1 and il.award_type != 'first_investee' and i.`type`= 8 and il.user_id = #{userId}
order by create_time desc
limit #{start},#{limit}
