mapper.select:

select

  distinct


from t_mall_activity_second_kill




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

SELECT mask.*, ma.name activity_name, ma.start_date, ma.stop_date, goods.id goods_id, goods.goods_name, goods.goods_no, goods.buying_price, goods.saling_price, goods.member_saling_price, goods.stock, goods.actvity_stock, goods.little_image, goods.activity_image FROM t_mall_activity_second_kill mask INNER JOIN t_mall_activity ma ON ma.id = mask.activity_id INNER JOIN (SELECT g.*, p14.path little_image, p16.path activity_image FROM t_goods g INNER JOIN (SELECT gp14.goods_id, u14.path FROM (SELECT MIN(goods_id) goods_id, id, upload_id FROM t_goods_picture WHERE `status`=0 AND `type`=14 GROUP BY goods_id) gp14 INNER JOIN upload u14 ON u14.id = gp14.upload_id AND u14.`status` = 0) p14 ON g.id = p14.goods_id LEFT JOIN (SELECT gp16.goods_id, u16.path FROM (SELECT MIN(goods_id) goods_id, id, upload_id FROM t_goods_picture WHERE `status`=0 AND `type`=16 GROUP BY goods_id) gp16 INNER JOIN upload u16 ON u16.id = gp16.upload_id AND u16.`status` = 0) p16 ON g.id = p16.goods_id) goods ON goods.id=mask.good_id where mask.enable=1
and mask.id=#{id}
and ma.id=#{mallActivityId}
and goods.goods_name like ${goodsName}
and mask.is_on_shelves like ${isOnShelves}
order by mask.id desc
limit #{limitStart}, #{limitEnd}

SELECT count(*) FROM t_mall_activity_second_kill mask INNER JOIN t_mall_activity ma ON ma.id = mask.activity_id INNER JOIN (SELECT g.*, p14.path little_image, p16.path activity_image FROM t_goods g INNER JOIN (SELECT gp14.goods_id, u14.path FROM (SELECT MIN(goods_id) goods_id, id, upload_id FROM t_goods_picture WHERE `status`=0 AND `type`=14 GROUP BY goods_id) gp14 INNER JOIN upload u14 ON u14.id = gp14.upload_id AND u14.`status` = 0) p14 ON g.id = p14.goods_id LEFT JOIN (SELECT gp16.goods_id, u16.path FROM (SELECT MIN(goods_id) goods_id, id, upload_id FROM t_goods_picture WHERE `status`=0 AND `type`=16 GROUP BY goods_id) gp16 INNER JOIN upload u16 ON u16.id = gp16.upload_id AND u16.`status` = 0) p16 ON g.id = p16.goods_id) goods ON goods.id=mask.good_id where mask.enable=1
and mask.id=#{id}
and ma.id=#{mallActivityId}
and goods.goods_name like ${goodsName}
and mask.is_on_shelves like ${isOnShelves}
