SELECT count(1) value,'hongbaoCount' name from yao_record y where y.type = 1
and y.time between #{startTime} and #{endTime}
UNION SELECT count(1) value ,'rateCouponCount' name from yao_record y where y.type = 2
and y.time between #{startTime} and #{endTime}
UNION SELECT count(1) value,'nothingCount' name from yao_record y where y.type = 0
and y.time between #{startTime} and #{endTime}
UNION SELECT COUNT(1) value,'leasthongbaoCount' name from yao_record y,hongbao h where y.type = 1 and y.other_id = h.id and h.amount < 0.1
and y.time between #{startTime} and #{endTime}
UNION SELECT COUNT(1) value,'mediumhongbaoCount' name from yao_record y,hongbao h where y.type = 1 and y.other_id = h.id and h.amount >=0.1 and h.amount < 1
and y.time between #{startTime} and #{endTime}
UNION SELECT COUNT(1) value,'bighongbaoCount' name from yao_record y,hongbao h where y.type = 1 and y.other_id = h.id and h.amount>=1
and y.time between #{startTime} and #{endTime}
UNION SELECT SUM(amount) value,'leasthongbaoAmount' name from yao_record y,hongbao h where y.type = 1 and y.other_id = h.id and h.amount < 0.1
and y.time between #{startTime} and #{endTime}
UNION SELECT SUM(amount) value,'mediumhongbaoAmount' name from yao_record y,hongbao h where y.type = 1 and y.other_id = h.id and h.amount >=0.1 and h.amount < 1
and y.time between #{startTime} and #{endTime}
UNION SELECT SUM(amount) value,'bighongbaoAmount' name from yao_record y,hongbao h where y.type = 1 and y.other_id = h.id and h.amount>=1
and y.time between #{startTime} and #{endTime}
UNION SELECT COUNT(1) value,'leastRateCouponCount' name from yao_record y,rate_coupon r where y.type = 2 and y.other_id = r.id and r.rate=0.001
and y.time between #{startTime} and #{endTime}
UNION SELECT COUNT(1) value,'littleRateCouponCount' name from yao_record y,rate_coupon r where y.type = 2 and y.other_id = r.id and r.rate=0.002
and y.time between #{startTime} and #{endTime}
UNION SELECT COUNT(1) value,'mediumRateCouponCount' name from yao_record y,rate_coupon r where y.type = 2 and y.other_id = r.id and r.rate=0.003
and y.time between #{startTime} and #{endTime}
UNION SELECT COUNT(1) value,'bigRateCouponCount' name from yao_record y,rate_coupon r where y.type = 2 and y.other_id = r.id and r.rate=0.004
and y.time between #{startTime} and #{endTime}
UNION SELECT COUNT(1) value,'biggestRateCouponCount' name from yao_record y,rate_coupon r where y.type = 2 and y.other_id = r.id and r.rate=0.005
and y.time between #{startTime} and #{endTime}
union SELECT COUNT(1) value,'eaRateCouponCount' name from yao_record y,rate_coupon r,assets a where y.type = 2 and y.other_id = r.id and r.rate=0.005 and y.user_id = a.user_id and (a.huo_investment_amount+a.available_balance) < 5000
and y.time between #{startTime} and #{endTime}
union SELECT COUNT(1) value,'ebRateCouponCount' name from yao_record y,rate_coupon r,assets a where y.type = 2 and y.other_id = r.id and r.rate=0.005 and y.user_id = a.user_id and (a.huo_investment_amount+a.available_balance)>=5000 and (a.huo_investment_amount+a.available_balance) < 20000
and y.time between #{startTime} and #{endTime}
union SELECT COUNT(1) value,'ecRateCouponCount' name from yao_record y,rate_coupon r,assets a where y.type = 2 and y.other_id = r.id and r.rate=0.005 and y.user_id = a.user_id and (a.huo_investment_amount+a.available_balance)>=20000 and (a.huo_investment_amount+a.available_balance) < 50000
